import React, { useEffect, useState } from 'react';
import './styles.css';
import Card from '../Card';
import {apiType} from '../../services/api';

export default function CardsContainer({addToCart}) {
  const [pokemonArray, setPokemonArray] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);

  useEffect(() => {
    async function loadPokemon() {
      const response = await apiType.get('/electric');
      setPokemonArray(response.data.pokemon);
    }
    loadPokemon();
  }, [])

  function addItem(currentPokemon){
    setSelectedItems([... currentPokemon]);
    return selectedItems;
  }
  return (
    <div className='container'>
      {pokemonArray.map(pokemon => {
        return <Card pokemon={pokemon} addToCart={()=> {return addItem(currentPokemon)}}/>
      })}
    </div>
  );
};
