import React, { useEffect, useState } from 'react';
import './styles.css';
import api from '../../services/api';
const pikachuImageUrl = 'https://i.pinimg.com/originals/76/47/9d/76479dd91dc55c2768ddccfc30a4fbf5.png';
const unknownSprite = 'https://images.vexels.com/media/users/3/155301/isolated/preview/6a91c0d6c8ba37a9fd115e1776300319-pergunta-do-doodle-do-ponto-de-interroga----o-3d-by-vexels.png';

export default function Card({ pokemon }) {
  const [currentPokemon, setCurrentPokemon] = useState({});
  const [pokemonSprite, setPokemonSprite] = useState(unknownSprite);

  useEffect(() => {
    async function loadPokemon() {
      const response = await api.get(pokemon.pokemon.url);
      setCurrentPokemon(response.data);

      try{
        setPokemonSprite(response.data.sprites.front_default);

    }catch{
    }
    }
    loadPokemon();

  }, [])

  return (
    <div className='card'>
      <img src={pokemonSprite.front_default}/>
      <text className='name'>{currentPokemon.name}</text>
      <p />
      <text className='price'>R$ </text>
      <button>Adicionar ao carrinho</button>
    </div>
  );
}
