import React, { useState, useEffect } from 'react';
import './styles.css';
const pikachuImageUrl = 'https://i.pinimg.com/originals/76/47/9d/76479dd91dc55c2768ddccfc30a4fbf5.png';

export default function Cart({ cartItems, totalPrice }) {
 function log(){ console.log(cartItems.map(item=>{ return item.sprites}));}
 log();
  return (
    <div className='cart'>
      <h1>Carrinho</h1>
      {
        cartItems.map(item => {
          return (
            <div className='item'>
              <text className='name'>{item.name}</text>
              <text className='price'>R$ {item.order}</text>
            </div>
          );
        })}
      <div className='bottomContent'>
        <h2>Total : R$ {totalPrice}</h2>
      </div>
    </div>
  );
};
