  const [currentSearchTerm, setSearchTerm] = useState(()=>{return searchTerm});
  const [newArraypokemon, setNewArrayPokemon] = useState([]);

  useEffect(() => {
    async function loadPokemon() {
        const response = await apiType.get('/electric');
        setPokemonArray(response.data.pokemon);
    }
    loadPokemon();

  }, [])

